import Row from 'react-bootstrap/lib/Row.js';
import Col from 'react-bootstrap/lib/Col.js';
import Button from 'react-bootstrap/lib/Button.js';







class MatchPage extends Component{
  constructor(props){
    super(props);

    this.state = {
      futureMatchIndex:0

    };
  }


  handleNext(){
    this.setState({futureMatchIndex: (this.state.futureMatchIndex + 1)});
  }

  handleLike(){
    this.setState({newLike: this.props.futureMatches[this.state.futureMatchIndex].username})
    this.props.returnLike(this.props.futureMatches[this.state.futureMatchIndex].username)
  }

  render(){



    return(
      <Grid>
        <Row bsClass="topRow">
          <Col><UserDetail currentLogin={this.props.futureMatches[this.state.futureMatchIndex]}/></Col>
        </Row>
        <Row>
          <Col>
            <Button onClick={()=>this.handleNext()} value="Next">Next</Button>
            <Button onClick={()=>this.handleLike()} value="Like">Like</Button>
          </Col>
        </Row>
      </Grid>


    );
  }
}



export default MatchPage;

/*
